import React from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import $ from 'jquery';
import 'bootstrap/dist/js/bootstrap.bundle.min';


//Parent Component
class App extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			userEmail: localStorage.getItem("email"),
			userToken: localStorage.getItem("token"),
			nav: "companies",
			companyList: [],
			companyName: "",
			newCompany: "",
			newName: "",
			address: "",
			newAddress: "",
			emailAddr: "",
			newEmailAddr: "",
			phone: "",
			newPhone: "",
			articles: [],
			apps: [],
			selectedApp: {},
			updatedApp: {},
			updatedAppName: "",
			updatedAppInstaller: "",
			updatedAppNotes: "",
			backups: {
				offsiteTechnology: "",
				offsiteWindow: "",
				offsiteFrequency: "",
				offsiteDescription: "",
				offsiteLocation: "",
				localTechnology: "",
				localWindow: "",
				localFrequency: "",
				localDescription: "",
				localLocation: ""
			},
			newOffsiteTechnology: "",
			newOffsiteWindow: "",
			newOffsiteFrequency: "",
			newOffsiteDescription: "",
			newOffsiteLocation: "",
			newLocalTechnology: "",
			newLocalWindow: "",
			newLocalFrequency: "",
			newLocalDescription: "",
			newLocalLocation: "",
			email: {
