									<input name="routerDhcp" id="flexSwitchCheckDefault" className="form-check-input" type="checkbox" checked={this.props.routerDhcp} value={this.props.routerDhcp} onChange={this.props.handleRouterDhcpChange}/>
								</form>
							</div>
							<div className="router-fields">
								<label htmlFor="routerNotes">Notes</label>
								<textarea name="routerNotes" type="text" value={this.props.routerNotes} onChange={this.props.handleNetworksChange}/>
							</div>
						</div>

					</div>
					<div id="wireless-card" className="network-card">
						<h2>Wireless</h2>
						<label htmlFor="wirelessSsid">SSID</label>
						<input name="wirelessSsid" type="text" value={this.props.wirelessSsid} onChange={this.props.handleNetworksChange}/>
						<label htmlFor="wirelessEncryption">Wireless Encryption</label>
						<input name="wirelessEncryption" type="text" value={this.props.wirelessEncryption} onChange={this.props.handleNetworksChange}/>
						<label htmlFor="wirelessMgmtURL">Management URL</label>
						<input name="wirelessMgmtURL" type="text" value={this.props.wirelessMgmtURL} onChange={this.props.handleNetworksChange}/>
					</div>
					<div id="vpn-card" className="network-card">
						<h2>VPN</h2>
						<label htmlFor="vpnType">VPN Type</label>
						<input name="vpnType" type="text" value={this.props.vpnType} onChange={this.props.handleNetworksChange}/>
						<label htmlFor="vpnPskLocation">PSK Location</label>
						<input name="vpnPskLocation" type="text" value={this.props.vpnPskLocation} onChange={this.props.handleNetworksChange}/>
						<label htmlFor="vpnPublicIP">VPN Server Address</label>
						<input name="vpnPublicIP" type="text" value={this.props.vpnPublicIP} onChange={this.props.handleNetworksChange}/>
						<label htmlFor="vpnClient">VPN Client</label>
						<input name="vpnClient" type="text" value={this.props.vpnClient} onChange={this.props.handleNetworksChange}/>
					</div>
				</div>

				<button id="router-save-button" onClick={this.props.updateNetworks}>Save</button>
			</div>
		)
	}
}

class Servers extends React.Component {
	constructor(props) {
		super(props);
	};

	render() {

		const server = this.props.servers.map((item,index) => 
			<div className="app-cards">
				<h3 className="app-title">
					<a>
						{item.hostname}
					</a>	
				</h3>
				<button onClick={() => {this.props.selectServer(item)}}>Select</button>
			</div>	
		)

		return (
			<div id="applications-container">
				<form id="app-info">
					<input name="updatedServerHostname" onChange={this.props.handleServerChange} value={this.props.updatedServerHostname} placeholder="Hostname" />
					<input name="updatedServerIP" onChange={this.props.handleServerChange} value={this.props.updatedServerIP} placeholder="IP Address" />
					<input name="updatedServerRole" onChange={this.props.handleServerChange} value={this.props.updatedServerRole} placeholder="Server Role"/>
					<textarea name="updatedServerNotes" onChange={this.props.handleServerChange} value={this.props.updatedServerNotes} placeholder="Server Notes"/>
					<button id="save-app-button" onClick={this.props.updateServer} className="btn btn-primary">Save</button>
					<button className="btn btn-danger" onClick={this.props.deleteServer}>Delete</button>

				</form>
				<div id="app-card-container">
					<div id="appList">
						{server}
					</div>
					<button id="create-app-button" onClick={this.props.createNewServer}>Create New Server</button>
				</div>
			</div>
		)
	}
}

export default App;
