import React from "react";


class App extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			name: "Afghan Kush",
			address: "",
			articles: [],
			router: {
				subnet: "",
				ipAddr: "",
				vpn: ""
			}
		};
	};

	fetchCompany() {
		fetch("http://127.0.0.1:1313/companies/" + this.state.name)
			.then(res => res.json())	
			.then(res => this.setState({
				address: res.address,
				articles: res.articles,
				router: res.router
			}))
	};

	updateArticle(artTitle,artContent) {
		this.setState({
			articles: [...this.state.articles, {title: artTitle,content: artContent}]
		});		
	};

//	postToExpress() {
//		const postOptions = {
//			method: "POST",
//			headers: {"Content-Type": "application/json"},
//			body: JSON.stringify(this.state.companies)
//		}
//		fetch()
//
//	}

	componentDidMount() {
		this.fetchCompany();		
	}

	render() {
		 return (
			<div>
				<Articles articles={this.state.articles}/>
			</div>
		 )
	}
};


class Articles extends React.Component {
	constructor(props) {
		super(props);
	}


	render() {
		const article = this.props.articles.map(item => <div><h3 className="article-title">{item.title}</h3><p className="article-content">{item.content}</p></div>)
		return (
			<div id="article-container">
				<ul>
					{article}
				</ul>

				<button>Add new article</button>
			</div>

		)
	}
}


export default App;
