import React from "react";

class App extends React.Component {

	constructor(props) {
		super(props);
		this.state = {
			name: "g",
			address: "",
			articles: [],
			routers: {
				subnet: "",
				ipAddr: "",
				vpn: ""
			}
		};
	};


	fetchCompany() {
		fetch("http://127.0.0.1:1313/companies/" + this.state.name)
			.then(res => res.json())	
			.then(res => this.setState({
				address: res.address,
				articles: res.articles,
				routers: res.routers
			}))
	}

	componentDidMount() {
		this.fetchCompany();		
	}

	render() {
		return (
			<div>
				<h1>{this.state.address}</h1>
			</div>
		)
	}
};


export default App;
