import React from "react";

class App extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			name: "g",
			address: "",
			articles: [],
			routers: {
				subnet: "",
				ipAddr: "",
				vpn: ""
			}
		};
	};

	fetchCompany() {
		fetch("http://127.0.0.1:1313/companies/" + this.state.name)
			.then(res => res.json())	
			.then(res => this.setState({
				address: res.address,
				articles: res.articles,
				routers: res.routers
			}))
	};

	componentDidMount() {
		this.fetchCompany();		
	}

	render() {
		 return (
			<div>
				<Articles articles={this.state.articles}/>
			</div>
		 )
	}
};

class Articles extends React.Component {
	constructor(props) {
		super(props)
	}

	showArticle() {

	}

	render() {
		const article = this.props.articles.map(item => <div><h3 className="article-title" onClick={this.showArticle()}>{item.title}</h3><p className="">{item.content}</p></div>)
		return (
			<div id="article-container">
				<ul>
					{article}
				</ul>

				<button>Add new article</button>
			</div>

		)
	}
}


export default App;
