	constructor(props) {
		super(props);
		this.state = {
			name: "Afghan Kush",
			address: "",
			articles: [],
			router: {
				subnet: "",
				ipAddr: "",
				vpn: ""
			}
		};
	};

	fetchCompany() {
		fetch("http://127.0.0.1:1313/companies/" + this.state.name)
			.then(res => res.json())	
			.then(res => this.setState({
				address: res.address,
				articles: res.articles,
