import React from "react";


class App extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			companyName: "Afghan Kush",
			address: "",
			articles: [],
			router: {
				subnet: "",
				ipAddr: "",
				vpn: ""
			}
		};
	};

	fetchCompany() {
		fetch("http://127.0.0.1:1313/companies/" + this.state.companyName)
			.then(res => res.json())	
			.then(res => this.setState({
				address: res.address,
				articles: res.articles,
				router: res.router
			}))
	};

	componentDidMount() {
		this.fetchCompany();		
	}

	render() {
		 return (
			<div>
			 	<h1>{this.state.companyName}</h1>
				<Articles companyName={this.state.companyName} articles={this.state.articles}/>
			</div>
		 )
	}
};

//Articles Section
class Articles extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			newTitle: "",
			newBody: ""
		};
		this.updateArticle = this.updateArticle.bind(this);
	}

	handleTitleChange(event) {
		this.setState({
			newTitle: event.target.value,
		});
	}



	updateArticle(event) {
		event.preventDefault();
		const requestOptions = {
			method: "POST",
			headers: {"Content-Type": "application/json"},
			body: JSON.stringify(event.target.value)
		};
		fetch("http://127.0.0.1:1313/companies/articles/" + this.props.companyName, requestOptions)
			.then(res => res.json())
	};

	render() {
		const article = this.props.articles.map(item => <div><h3 className="article-title">{item.title}</h3><p className="article-content">{item.content}</p></div>)
		return (
			<div id="article-container">
				<form onSubmit={this.updateArticle}>
					<input value={this.state.newTitle} type="text" />
					<textarea value={this.state.newBody} name="" id="" cols="30" rows="10"></textarea>
					<button type="submit" >Add new article</button>
				</form>
				<ul>
					{article}
				</ul>
			</div>

		)
	}
}


export default App;
